import csv
import rottentomatoes as rt

def transform_to_csv(movies):
    csv_lines = []
    for index, movie in enumerate(movies, start=1):
        # Extrair os dados do filme
        titulo = movie.movie_title
        nota = movie.tomatometer
        genres = ", ".join(movie.genres)
        ano = movie.year_released
        atores = ",".join(movie.actors)
        diretores = ",".join(movie.directors)
        
        # Criar a linha CSV com o índice do filme
        csv_line = f"{index};{titulo};{nota};{genres};{ano};{atores};{diretores}"
        csv_lines.append(csv_line)
        print(csv_line)
        print(titulo + " adicionado ao csv")
    
    return "\n".join(csv_lines)

